// selectUtils.js

// Normalization of a manifest string.
//
// The argument should be a string (not a json object). 
// Normalizations removes new lines and stripps of leading and
// trailing spaces.
//

exports.normalizeManifest = function normalizeManifest(manifestString) {
    return manifestString.replace(/\n/g, '').replace(/\r/g, '').trim(' ');
}


// Converts time difference (in milliseconds) into a human 
// readable string.
//
exports.timeDelta2String = function timeDelta2String(delta) {
    if (delta<0) return '-'
    var delta_sec = Math.floor(delta/1000);
    var sec = delta_sec % 60;
    var delta_min = Math.floor(delta_sec / 60);
    var min = delta_min % 60;
    var hou = Math.floor(delta_min / 60);
    return hou + ' hours ' + min + ' min ' + sec + ' sec';
}

